import React, { useState } from "react";
import InputForm from "./components/InputForm";

function App() {
  const [result, setResult] = useState(null);
  const [page, setPage] = useState("home"); // home, about, contact
  
  return (
    <div style={styles.page}>
      <div style={styles.card}>
        <h1 style={styles.title}>ðŸŽ“ Student Performance Prediction</h1>
        <p style={styles.subtitle}>
          Enter the student details to predict the final grade
        </p>

        {/* Input Form */}
        <InputForm onResult={setResult} />

        {/* Prediction Result Box */}
{result && typeof result === "string" && (
  <div style={styles.resultBox}>
    <h3 style={styles.resultTitle}>ðŸ“Š Prediction Result</h3>
    <p style={styles.resultText}>
      Predicted Grade: <b>{result}</b>
    </p>
  </div>
)}

      </div>
    </div>
  );
}

const styles = {
  page: {
    background: "linear-gradient(120deg, #6a11cb, #2575fc)",
    minHeight: "100vh",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    padding: "20px",
  },
  card: {
    background: "#fff",
    padding: "30px",
    borderRadius: "15px",
    maxWidth: "600px",
    width: "100%",
    boxShadow: "0px 8px 25px rgba(0,0,0,0.15)",
    textAlign: "center",
  },
  title: {
    fontSize: "30px",
    color: "#333",
    marginBottom: "10px",
  },
  subtitle: {
    color: "#666",
    fontSize: "16px",
    marginBottom: "25px",
  },
  resultBox: {
    marginTop: "25px",
    padding: "15px",
    background: "#e8f0fe",
    borderRadius: "10px",
    borderLeft: "5px solid #2575fc",
  },
  resultTitle: {
    margin: 0,
    fontSize: "18px",
    fontWeight: "600",
    color: "#2575fc",
  },
  resultText: {
    fontSize: "20px",
    marginTop: "8px",
    color: "#333",
  },
};

export default App;
