import React, { useState } from "react";
import InputForm from "./components/InputForm";
import Home from "./pages/Home";
import About from "./pages/About";
import Contact from "./pages/Contact";
function App() {
  const [result, setResult] = useState(null);
  const [page, setPage] = useState("home"); // home, about, contact
   
  const renderPage = () => {
    switch (page) {
      case "home":
        return <Home onResult={setResult} result={result} />;
      case "about":
        return <About />;
      case "contact":
        return <Contact />;
      default:
        return <Home onResult={setResult} result={result} />;
    }
  };
  return (
    <div style={styles.page}>
      <div style={styles.card}>
        <h1 style={styles.title}>ðŸŽ“ Student Performance Prediction</h1>
        <p style={styles.subtitle}>
          Enter the student details to predict the final grade
        </p>

        {/* Input Form */}
        <InputForm onResult={setResult} />

        {/* Prediction Result Box */}
{result && typeof result === "string" && (
  <div style={styles.resultBox}>
    <h3 style={styles.resultTitle}>ðŸ“Š Prediction Result</h3>
    <p style={styles.resultText}>
      Predicted Grade: <b>{result}</b>
    </p>
  </div>
)}
 
    {/* Navigation */}
      <nav style={styles.nav}>
        <h2 style={{ color: "#fff" }}>Student Performance App</h2>
        <div>
          <button style={styles.link} onClick={() => setPage("home")}>Home</button>
          <button style={styles.link} onClick={() => setPage("about")}>About</button>
          <button style={styles.link} onClick={() => setPage("contact")}>Contact</button>
        </div>
      </nav>

      {/* Render selected page */}
      <div style={styles.pageContent}>
        {renderPage()}
      </div>
    </div>
      </div>
  );
}

const styles = {
  page: {
    background: "linear-gradient(120deg, #6a11cb, #2575fc)",
    minHeight: "100vh",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    padding: "20px",
  },
  card: {
    background: "#fff",
    padding: "30px",
    borderRadius: "15px",
    maxWidth: "600px",
    width: "100%",
    boxShadow: "0px 8px 25px rgba(0,0,0,0.15)",
    textAlign: "center",
  },
  title: {
    fontSize: "30px",
    color: "#333",
    marginBottom: "10px",
  },
  subtitle: {
    color: "#666",
    fontSize: "16px",
    marginBottom: "25px",
  },
  resultBox: {
    marginTop: "25px",
    padding: "15px",
    background: "#e8f0fe",
    borderRadius: "10px",
    borderLeft: "5px solid #2575fc",
  },
  resultTitle: {
    margin: 0,
    fontSize: "18px",
    fontWeight: "600",
    color: "#2575fc",
  },
  resultText: {
    fontSize: "20px",
    marginTop: "8px",
    color: "#333",
  },
  nav: {
    display: "flex",
    justifyContent: "space-between",
    padding: "15px 50px",
    background: "#1e88e5",
    alignItems: "center",
  },
  link: {
    color: "#fff",
    marginLeft: "20px",
    fontWeight: "bold",
    cursor: "pointer",
    background: "transparent",
    border: "none",
    fontSize: "16px",
  },
};

export default App;
