import React, { useState } from "react";
import InputForm from "./components/InputForm";

function App() {
    
  const [result, setResult] = useState(null);

  // This function receives result from InputForm
  const handleResult = (predictionData) => {
    setResult(predictionData);
  };

  return (
    
    <div style={styles.container}>
      <h1 style={styles.title}>Student Performance Prediction</h1>
      <p style={styles.subtitle}>
        Enter student details below to predict the final grade (A/B/C/D)
      </p>

      {/* Pass handleResult to InputForm */}
      <InputForm onResult={handleResult} />

      {/* Show prediction result */}
      {result && (
        <div style={styles.resultBox}>
          <h2>Predicted Grade:</h2>
          <p style={styles.prediction}>{result.prediction}</p>
        </div>
      )}
    </div>
  );
}

const styles = {
  container: {
    maxWidth: "700px",
    margin: "auto",
    padding: "20px",
    textAlign: "center",
    fontFamily: "Arial, sans-serif",
  },
  title: {
    fontSize: "32px",
    marginBottom: "10px",
  },
  subtitle: {
    fontSize: "16px",
    color: "#555",
    marginBottom: "20px",
  },
  resultBox: {
    marginTop: "30px",
    padding: "15px",
    backgroundColor: "#f0f0f0",
    borderRadius: "8px",
  },
  prediction: {
    fontSize: "24px",
    fontWeight: "bold",
    color: "#333",
  },
};

export default App;
